svg.script:
const svg=document.documentElement;
function rand(n){return Math.random()*n}
function color(){return `hsl(${rand(360)},80%,50%)`}
svg.addEventListener('click',()=>{
  const isCircle=Math.random()<0.5;
  if(isCircle){
    let c=document.createElementNS(svg.namespaceURI,'circle');
    c.setAttribute('cx',rand(320));c.setAttribute('cy',rand(240));
    c.setAttribute('r',5+rand(25));c.setAttribute('fill',color());
    svg.appendChild(c);
  }else{
    let r=document.createElementNS(svg.namespaceURI,'rect');
    r.setAttribute('x',rand(300));r.setAttribute('y',rand(220));
    let s=10+rand(30);r.setAttribute('width',s);r.setAttribute('height',s);
    r.setAttribute('fill',color());
    svg.appendChild(r);
  }
});
