svg.script:
const wave = document.getElementById('wave');
let phase = 0;
function draw(){
  const W = 480, H = 180, mid = H/2, A = 40;
  const pts = [];
  for (let x = 0; x <= W; x += 4){
    const y = mid + A * Math.sin((x/58) + phase) * Math.cos((x/91) - phase * 0.7);
    pts.push((x===0?'M':'L') + x + ' ' + y.toFixed(2));
  }
  wave.setAttribute('d', pts.join(''));
  phase += 0.02;
  requestAnimationFrame(draw);
}
draw();
